	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $photo_id
	   @param      string $normalized_tag
	   
	 * @param      PropelPDO $con
	 * @return     PhotoTag
	 */
	public static function retrieveByPK($photo_id, $normalized_tag, PropelPDO $con = null) {
		$key = serialize(array((string) $photo_id, (string) $normalized_tag));
 		if (null !== ($obj = PhotoTagPeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(PhotoTagPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(PhotoTagPeer::DATABASE_NAME);
		$criteria->add(PhotoTagPeer::PHOTO_ID, $photo_id);
		$criteria->add(PhotoTagPeer::NORMALIZED_TAG, $normalized_tag);
		$v = PhotoTagPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
