    {
        $partial = $this->collGameUsersPartial && !$this->isNew();
        if (null === $this->collGameUsers || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collGameUsers) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getGameUsers());
                }
                $query = GameUserQuery::create(null, $criteria);
                if ($distinct) {
