     * @param      PropelPDO $con the connection to use
     * @return Guest
     */
    public static function retrieveByPK($pk, PropelPDO $con = null)
    {

        if (null !== ($obj = GuestPeer::getInstanceFromPool((string) $pk))) {
            return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(GuestPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }

        $criteria = new Criteria(GuestPeer::DATABASE_NAME);
        $criteria->add(GuestPeer::ID, $pk);

        $v = GuestPeer::doSelect($criteria, $con);

        return !empty($v) > 0 ? $v[0] : null;
    }

