    /**
     * Build a Criteria object containing the values of all modified columns in this object.
     *
     * @return Criteria The Criteria object containing all modified values.
     */
    public function buildCriteria()
    {
        $criteria = new Criteria(UpdatesPeer::DATABASE_NAME);

        if ($this->isColumnModified(UpdatesPeer::PID)) $criteria->add(UpdatesPeer::PID, $this->pid);
        if ($this->isColumnModified(UpdatesPeer::ID)) $criteria->add(UpdatesPeer::ID, $this->id);
        if ($this->isColumnModified(UpdatesPeer::USER_ID)) $criteria->add(UpdatesPeer::USER_ID, $this->user_id);
        if ($this->isColumnModified(UpdatesPeer::FRIEND_ID)) $criteria->add(UpdatesPeer::FRIEND_ID, $this->friend_id);
        if ($this->isColumnModified(UpdatesPeer::GROUP_ID)) $criteria->add(UpdatesPeer::GROUP_ID, $this->group_id);
        if ($this->isColumnModified(UpdatesPeer::F_STATUS_NAME)) $criteria->add(UpdatesPeer::F_STATUS_NAME, $this->f_status_name);
        if ($this->isColumnModified(UpdatesPeer::P_OWNER_ID)) $criteria->add(UpdatesPeer::P_OWNER_ID, $this->p_owner_id);
        if ($this->isColumnModified(UpdatesPeer::NUM_COMMENT)) $criteria->add(UpdatesPeer::NUM_COMMENT, $this->num_comment);
        if ($this->isColumnModified(UpdatesPeer::DESCRIPTION)) $criteria->add(UpdatesPeer::DESCRIPTION, $this->description);
        if ($this->isColumnModified(UpdatesPeer::CREATED_AT)) $criteria->add(UpdatesPeer::CREATED_AT, $this->created_at);

        return $criteria;
    }

