                    if (false !== $this->collVideolistYtvideosPartial && count($collVideolistYtvideos)) {
                      $this->initVideolistYtvideos(false);

                      foreach($collVideolistYtvideos as $obj) {
                        if (false == $this->collVideolistYtvideos->contains($obj)) {
                          $this->collVideolistYtvideos->append($obj);
                        }
                      }

                      $this->collVideolistYtvideosPartial = true;
                    }

                    return $collVideolistYtvideos;
                }

                if($partial && $this->collVideolistYtvideos) {
                    foreach($this->collVideolistYtvideos as $obj) {
                        if($obj->isNew()) {
                            $collVideolistYtvideos[] = $obj;
                        }
