        }

        $this->collVideolistYtvideos = null;
        foreach ($videolistYtvideos as $videolistYtvideo) {
            $this->addVideolistYtvideo($videolistYtvideo);
        }

        $this->collVideolistYtvideos = $videolistYtvideos;
        $this->collVideolistYtvideosPartial = false;
