    {
        $partial = $this->collVideolistYtvideosPartial && !$this->isNew();
        if (null === $this->collVideolistYtvideos || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collVideolistYtvideos) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getVideolistYtvideos());
                }
                $query = VideolistYtvideoQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByVideolist($this)
                    ->count($con);
