    {
        if ($this->getVideolistYtvideos()->contains($videolistYtvideo)) {
            $this->collVideolistYtvideos->remove($this->collVideolistYtvideos->search($videolistYtvideo));
            if (null === $this->videolistYtvideosScheduledForDeletion) {
                $this->videolistYtvideosScheduledForDeletion = clone $this->collVideolistYtvideos;
                $this->videolistYtvideosScheduledForDeletion->clear();
            }
            $this->videolistYtvideosScheduledForDeletion[]= $videolistYtvideo;
            $videolistYtvideo->setVideolist(null);
        }
    }

